(3, 'ALL', 'Albania Lek', 'Cộng hòa Albania', NULL, '/storage/currency/Lek.png', '&#8356;', '2019-03-05 07:08:09', '2019-03-05 07:44:16'),
(4, 'AMD', 'Armenia Dram', '', NULL, NULL, '', '2019-03-05 07:08:09', '2019-03-05 07:08:09'),
(5, 'ANG', 'Netherlands Antilles Guilder', '', NULL, NULL, '', '2019-03-05 07:08:09', '2019-03-05 07:08:09'),
