INSERT INTO `currencies_code` (`id`, `code`, `name`, `vname`, `description`, `icon`, `symbol`, `created_at`, `updated_at`) VALUES
(140, 'TWD', 'Taiwan New Dollar', 'Đô la Đài Loan', '<p><strong>T&acirc;n Đ&agrave;i Tệ</strong> (phồn thể: 新臺幣 hoặc 新台幣; b&iacute;nh &acirc;m: Xīnt&aacute;ib&igrave; , nghĩa l&agrave; Tiền Đ&agrave;i Loan mới, m&atilde; tiền tệ TWD v&agrave; viết tắt th&ocirc;ng thường l&agrave; NT$), hay đơn giản l&agrave; Đ&ocirc; la Đ&agrave;i Loan (臺幣) (Đ&agrave;i tệ), l&agrave; đơn vị tiền tệ ch&iacute;nh thức của Trung Hoa D&acirc;n Quốc b&ecirc;n trong l&atilde;nh thổ Đ&agrave;i Loan, v&agrave; c&aacute;c đảo B&agrave;nh Hồ, Kim M&ocirc;n, v&agrave; M&atilde; Tổ kể từ năm 1949. Ban đầu được ph&aacute;t h&agrave;nh bởi Ng&acirc;n h&agrave;ng Đ&agrave;i Loan, hiện nay bởi Ng&acirc;n h&agrave;ng Trung ương Trung Hoa D&acirc;n Quốc.</p>\r\n\r\n<p><strong>C&aacute;c mệnh gi&aacute; đang &aacute;p dụng trong lưu th&ocirc;ng:</strong></p>\r\n\r\n<ul>\r\n	<li>C&oacute; 5 mệnh gi&aacute; tiền xu cho Đ&ocirc; la Đ&agrave;i Loan mới ( $1 , $5 , $10 , $20 v&agrave; $50 ),</li>\r\n	<li>Đ&ocirc; la Đ&agrave;i Loan mới c&oacute; 5 mệnh gi&aacute; tiền giấy ( $100 , $200 , $500 , $1000 v&agrave; $2000 )</li>\r\n</ul>\r\n\r\n<p>Web tỷ gi&aacute; cập nhật li&ecirc;n tục, ch&iacute;nh x&aacute;c tỷ gi&aacute; mua, b&aacute;n, chuyển khoản <strong>T&acirc;n Đ&agrave;i tệ (TWD</strong>) từ c&aacute;c ng&acirc;n h&agrave;ng lớn c&oacute; hỗ trợ giao dịch T&acirc;n Đ&agrave;i tệ (TWD). cung cấp c&aacute;c c&ocirc;ng cụ chuyển đổi, quy đổi <strong>T&acirc;n Đ&agrave;i tệ (TWD)</strong> ra c&aacute;c đồng tiền kh&aacute;c</p>\r\n\r\n<ul>\r\n	<li>tiền đ&agrave;i loan</li>\r\n	<li>tỷ gi&aacute; đ&agrave;i tệ</li>\r\n	<li>twd to vnd</li>\r\n	<li>đổi tiền đ&agrave;i loan</li>\r\n	<li>1 twd = vnd</li>\r\n</ul>', '', '角', '2019-03-05 07:08:09', '2019-05-28 02:05:16'),
