INSERT INTO `news` (`id`, `title`, `news_slug`, `short_description`, `content`, `author`, `author_email`, `image`, `language`, `custom_layout`, `view_count`, `seo`, `status`, `publish_date`, `footer_script`, `created_at`, `updated_at`) VALUES
(1211, 'Giới thiệu', 'gioi-thieu-9', 'Với tất cả niềm đam mê và lòng nhiệt huyết, TyGia.vn sẽ luôn cố gắng cập nhật nhanh nhất, mới nhất những thông tin về thị trường tài chính đến mọi người.', '<p>Với mục đích cập nhật dữ liệu về giá vàng, tỷ giá ngân hàng, tỷ giá ngoại tệ của các tổ chức, ngân hàng tại Việt Nam. Qua thời gian phát triển, nhận được nhiều ý kiến đóng góp từ độc giả, Web Giá đã ngày càng nâng cấp và hoàn thiện hơn, thêm nhiều chức năng mới, nhằm phục vụ nhu cầu thông tin của mọi người.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Giới thiệu Web Giá - tygia.vn\" src=\"https://images2-focus-opensocial.googleusercontent.com/gadgets/proxy?container=focus&gadget=a&no_expand=1&rewriteMime=image/*&resize_w=600&url=https://i.imgur.com/Jvqb030.jpg\" style=\"margin:10px auto\" /></p>\r\n\r\n<p>Hình ảnh cho bài viết Giới thiệu Tỷ Giá - Tygia.vn</p>\r\n\r\n<p> </p>\r\n\r\n<h2>Giới thiệu Tỷ Giá</h2>\r\n\r\n<p><strong>Tygia.vn</strong> hay được gọi là <strong>Tỷ Giá</strong>, được thành lập vào tháng 08 năm 2015.</p>\r\n\r\n<p>Với mục đích ban đầu là cập nhật dữ liệu về giá vàng, tỷ giá ngân hàng, tỷ giá ngoại tệ của các tổ chức, ngân hàng tại Việt Nam.</p>\r\n\r\n<p>Qua thời gian phát triển, nhận được nhiều ý kiến đóng góp từ độc giả, <strong>Tỷ Giá</strong> đã ngày càng nâng cấp và hoàn thiện hơn, thêm nhiều chức năng mới, nhằm phục vụ nhu cầu thông tin của mọi người.</p>\r\n\r\n<h2>Chức năng nổi bật</h2>\r\n\r\n<p><strong>Tygia.vn</strong> ngày nay đã có những chức năng nổi bật như:</p>\r\n\r\n<p>+ Cập nhật <a href=\"http://tygia.vn/gia-vang/\">giá vàng</a>, <a href=\"http://tygia.vn/ty-gia/\">tỷ giá ngân hàng</a>, <a href=\"http://tygia.vn/ngoai-te/\">tỷ giá ngoại tệ</a>, giá xăng dầu với thời gian nhanh nhất.</p>\r\n\r\n<p>+ Tra cứu <a href=\"http://tygia.vn/ty-gia/vietcombank/\">lịch sử tỷ giá ngoại tệ</a> (Hỗ trợ ngân hàng Vietcombank)</p>\r\n\r\n<p>+ <a href=\"http://tygia.vn/gia-vang/\">So sánh giá vàng</a> tại các tổ chức lớn tại Việt Nam như SJC, DOJI, Bảo Tín Minh Châu, PNJ, Phú Quý... giúp người dùng có thể xem <a href=\"http://tygia.vn/gia-vang/\">giá vàng mua ở đâu rẻ nhất</a>, bán vàng ở đâu giá tốt nhất. Đồng thời, chức năng xem lại lịch sử giá vàng SJC cũng đã được thêm vào, giúp người dùng có thể <a href=\"http://tygia.vn/gia-vang/sjc/\">tra cứu lịch sử giá vàng SJC</a>.</p>\r\n\r\n<p>+ <a href=\"http://tygia.vn/ngoai-te/\">So sánh tỷ giá ngoại tệ</a> (với 25 mã ngoại tệ) tại các ngân hàng Việt Nam, giúp người dùng có thể xem mua ngoại tệ ở ngân hàng nào giá tốt nhất và bán ngoại tệ ở ngân hàng nào giá tốt nhất.</p>\r\n\r\n<p>+ Biểu đồ <a href=\"http://tygia.vn/gia-vang/the-gioi/\">giá vàng thế giới</a>, <a href=\"http://tygia.vn/gia-xang-dau/dau-tho/\">giá dầu thô thế giới</a> theo thời gian thực.</p>\r\n\r\n<h2>Đôi lời tác giả</h2>\r\n\r\n<p>Với tất cả niềm đam mê và lòng nhiệt huyết, <strong>TyGia.vn </strong>sẽ luôn cố gắng cập nhật nhanh nhất, mới nhất những thông tin về thị trường tài chính đến mọi người.</p>\r\n\r\n<p>Hi vọng, luôn nhận được nhiều ý kiến, đóng góp để giúp <strong>Tỷ Giá</strong> ngày càng hoàn thiện và phát triển.</p>\r\n\r\n<p>Mọi ý kiến trao đổi, góp ý, các bạn có thể liên hệ với <strong>Tỷ Giá</strong> trang <a href=\"http://beta.tygia.vn/lien-he\">Liên hệ</a></p>', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/gioi-thieu-ve-chung-toi.jpg', 'vi', NULL, 1, 3150, 1, NULL, NULL, '2018-04-25 02:29:49', '2019-06-29 02:11:50'),
(1212, 'Chính sách bảo mật', 'chinh-sach-bao-mat-10', 'Xin vui lòng đọc chính sách bảo mật một cách cẩn thận để biết rõ về những thông tin thông tin cá nhân nào của bạn được thu thập, sử dụng, bảo vệ hoặc xử lý tại TyGia.vn', '<p><span style=\"color:#222222\"><a href=\"https://tygia.vn\">Tỷ Giá</a> là website tra cứu giá vàng, tỷ giá, so sánh tỷ giá miễn phí, đi kèm đó, luôn có những chính sách bảo mật đảm bảo cho khách truy cập.</span></p>\r\n\r\n<p><span style=\"color:#222222\">Xin vui lòng đọc chính sách bảo mật một cách cẩn thận để biết rõ về những thông tin thông tin cá nhân nào của bạn được thu thập, sử dụng, bảo vệ hoặc xử lý tại TyGia.vn</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Tỷ Giá thu thập thông tin cá nhân nào?</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">Tỷ Giá không thu thập bất kỳ thông tin cá nhân nào từ khách truy cập.</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Bảo vệ thông tin khách truy cập</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">TyGia.vn sử dụng chứng chỉ SSL được chứng nhận bởi tổ chức COMODO trong kết nối. Đảm bảo bạn luôn an toàn khi truy cập vào </span><a href=\"https://tygia.vn\">https://tygia.vn</a></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Việc sử dụng ‘Cookie’?</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">TyGia.vn không sử dụng cookie để lưu trữ thông tin về khách truy cập, theo dõi người dùng, thông tin người dùng cụ thể ghi trên các trang người dùng truy cập hoặc truy cập, tùy chỉnh nội dung trang web dựa trên loại khách truy cập trình duyệt hoặc các thông tin khác mà người truy cập sẽ gửi thông qua trình duyệt của họ.</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Các bên thứ ba</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">Không thu thập thông tin khách truy cập nên TyGia.vn cũng không có bán, trao đổi, hoặc chuyển giao các thông tin của bạn cho bất kỳ bên thứ ba nào.</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Các chương trình liên kết</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">TyGia.vn có thể cung cấp tin khuyến mại, giảm giá của các bên thứ ba trên Website. Những trang web của các bên thứ ba có chính sách bảo mật riêng biệt và độc lập. Do đó, TyGia.vn không có trách nhiệm đối với các nội dung của các trang website được liên kết.</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Quảng cáo của Google.</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">TyGia.vn có thể đăng tải, sử dụng Quảng cáo Google AdSense. Google là một nhà cung cấp bên thứ ba, sử dụng cookie để phục vụ quảng cáo trên Website TyGia.vn. Người dùng có thể chọn không tham gia sử dụng cookie của Google bằng cách truy cập các chính sách bảo mật quảng cáo và mạng nội dung của Google.</span></p>\r\n\r\n<p><span style=\"color:#222222\">Xem </span><a href=\"https://tygia.vn/gia-vang/pnj\">giá vàng nữ trang pnj</a> hôm nay</p>\r\n\r\n<p><span style=\"color:#222222\">TyGia.vn cùng với các nhà cung cấp bên thứ ba, chẳng hạn như Google sử dụng cookie của bên thứ ba (như các cookie Google Analytics) và các tập tin cookie của bên thứ ba (như cookie DoubleClick) để quản lý dữ liệu liên quan đến tương tác người dùng với hiển thị quảng cáo, và các chức năng dịch vụ quảng cáo khác có liên quan đến TyGia.vn</span></p>\r\n\r\n<p><strong><span style=\"color:#222222\">Liên hệ với TyGia.vn</span></strong></p>\r\n\r\n<p><span style=\"color:#222222\">Nếu có bất kỳ câu hỏi liên quan đến chính sách bảo mật, hãy gửi thông tin đến TyGia.vn qua trang Liên Hệ.</span></p>', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/chinhsach.jpg', 'vi', NULL, 1, 3151, 1, NULL, NULL, '2018-04-29 21:22:00', '2019-07-04 10:09:26'),
(1213, 'Điều khoản sử dụng', 'dieu-khoan-su-dung-11', 'Các điều khoản và điều kiện sau đây (gọi chung là các “Điều khoản sử dụng”) quy định việc truy cập và sử dụng các dịch vụ, thông tin từ Tygia.vn\r\n\r\nĐiều khoản sử dụng\r\n\r\nHình ảnh cho bài viết Điều khoản sử dụng\r\n\r\n \r\n\r\nChào mừng bạn đến với Tygia.vn\r', '<p>Các điều khoản và điều kiện sau đây (gọi chung là các “Điều khoản sử dụng”) quy định việc truy cập và sử dụng các dịch vụ, thông tin từ Tygia.vn</p>\r\n\r\n<p>Điều khoản sử dụng</p>\r\n\r\n<p>Hình ảnh cho bài viết Điều khoản sử dụng</p>\r\n\r\n<p> </p>\r\n\r\n<p>Chào mừng bạn đến với Tygia.vn</p>\r\n\r\n<p>Cảm ơn bạn đã truy cập <a href=\"https://tygia.vn\">Tygia.vn</a>, website được sở hữu bởi Tỷ Giá. Các điều khoản và điều kiện sau đây (gọi chung là các “Điều khoản sử dụng”) quy định việc truy cập và sử dụng các dịch vụ, thông tin từ Tygia.vn. Bạn hãy đọc các Điều khoản sử dụng này trước khi sử dụng các dịch vụ của Tygia.vn.</p>\r\n\r\n<p> </p>\r\n\r\n<p>Điều khoản sử dụng Tygia.vn</p>\r\n\r\n<p>1. Chấp nhận các Điều khoản sử dụng</p>\r\n\r\n<p>Bằng việc sử dụng bất kỳ dịch vụ nào của Tygia.vn, bạn chấp nhận, đồng ý bị ràng buộc và tuân thủ các điều khoản sử dụng của Tygia.vn dưới đây. Nếu bạn không đồng ý với bất kỳ điều khoản sử dụng, chính sách bảo mật của Tygia.vn hoặc không có đầy đủ năng lực hành vi dân sự phù hợp, xin vui lòng dừng việc truy cập và sử dụng Tygia.vn.</p>\r\n\r\n<p>Xem <a href=\"https://tygia.vn/ty-gia/vietcombank\" title=\"tỷ giá php vnd vietcombank\">tỷ giá php vnd vietcombank</a> 24/7</p>\r\n\r\n<p>2. Truy cập và sử dụng Tygia.vn</p>\r\n\r\n<p>Tygia.vn bảo lưu quyền được chấm dứt, thay đổi bất kỳ dịch vụ, thông tin nào mà Tygia.vn cung cấp vào bất cứ lúc nào, theo quyết định của Tygia.vn mà không cần báo trước.</p>\r\n\r\n<p> </p>\r\n\r\n<p>Tygia.vn cung cấp miễn phí thông tin đến mọi người. Không được truy cập trái phép vào các trang không công khai, bằng phương pháp nào đó không thông qua giao diện và hướng dẫn mà chúng tôi cung cấp. Chúng tôi có thể tạm ngừng hoặc ngừng cung cấp Dịch vụ của chúng tôi cho bạn nếu bạn không tuân thủ các điều khoản hoặc chính sách của chúng tôi hoặc nếu chúng tôi đang điều tra hành vi bị nghi ngờ là sai phạm.</p>\r\n\r\n<p> </p>\r\n\r\n<p>3. Quyền sở hữu trí tuệ</p>\r\n\r\n<p>Các Điều khoản sử dụng cho phép bạn sử dụng các dịch vụ của Tygia.vn cho cá nhân, cho mục đích phi thương mại. Bạn không được sao chép, phân phối, sửa đổi, hiển thị công khai, thực hiện công khai, tái xuất bản, tải về, lưu trữ hoặc truyền tải bất kỳ nội dung hoặc tài liệu có sẵn trên Website Tygia.vn ngoại trừ trường hợp việc này được thực hiện tự động bởi máy tính/ trình duyệt web mà bạn sử dụng, cần thiết cho việc truy cập và sử dụng các dịch vụ, thông tin của Tygia.vn.</p>\r\n\r\n<p> </p>\r\n\r\n<p>4. Thương hiệu</p>\r\n\r\n<p>Bạn không được sử dụng thương hiệu Tygia.vn, mà không có sự cho phép trước bằng văn bản của Tygia.vn. Các thương hiệu của các bên thứ ba, các biểu tượng, sản phẩm hoặc dịch vụ, thiết kế hay khẩu hiệu xuất hiện trên Tygia.vn không nhất thiết chỉ ra bất kỳ sự liên kết nào của các bên thứ ba đó với Tygia.vn.</p>\r\n\r\n<p> </p>\r\n\r\n<p>5. Hành vi bị nghiêm cấm</p>\r\n\r\n<p>Bạn chỉ có thể sử dụng các dịch vụ của Tygia.vn cho các mục đích hợp pháp và phù hợp với các điều khoản sử dụng. Bạn đồng ý không sử dụng các dịch vụ của Tygia.vn cho bất kỳ hành vi hoặc mục đích vi phạm pháp luật.</p>\r\n\r\n<p> </p>\r\n\r\n<p>6. Đóng góp của thành viên</p>\r\n\r\n<p>Bất kỳ thông tin nào bạn gửi đến các Liên hệ của Tygia.vn sẽ không được coi là các thông tin bảo mật. Bằng cách cung cấp bất kỳ đóng góp nào đến Tygia.vn, bạn cấp cho Tygia.vn quyền không thể thu hồi, đầy đủ, vĩnh viễn, và miễn phí để tái xuất bản, trưng bày, phân phối, sửa đổi. Bạn bảo đảm rằng bạn sở hữu hoặc kiểm soát mọi quyền đối với các thông tin đóng góp cho Tygia.vn. Bạn đảm bảo tính hợp pháp, độ tin cậy, tính chính xác và phù hợp của các thông tin đó. Tygia.vn không chịu trách nhiệm với bất kỳ bên thứ ba nào, về nội dung hoặc tính chính xác của bất kỳ đóng góp nào của người dùng.</p>\r\n\r\n<p> </p>\r\n\r\n<p>7. Giám sát và thi hành</p>\r\n\r\n<p>Tygia.vn có quyền: loại bỏ hoặc từ chối đưa lên bất kỳ thông tin đóng góp nào của thành viên theo đánh giá của Tygia.vn; thực hiện các sửa đổi mà Tygia.vn cho là cần thiết đối với các đóng góp của thành viên; tiết lộ danh tính của bạn hoặc các thông tin khác về bạn theo các yêu cầu của các cơ quan chức năng có thẩm quyền; chấm dứt hoặc đình chỉ truy cập của bạn đến tất cả hay một phần của Dịch vụ Tygia.vn cho bất kỳ hoặc không vì lý do gì.</p>\r\n\r\n<p> </p>\r\n\r\n<p>8. Tiêu chuẩn Nội dung</p>\r\n\r\n<p>Nội dung trên Tygia.vn được cập nhật liên tục từ trang website chính thức của các ngân hàng, tổ chức. Mọi thông tin về tính chính xác của nội dung cũng như trách nhiệm về nội dung vui lòng đọc tại đây.</p>\r\n\r\n<p> </p>\r\n\r\n<p>9. Vi phạm bản quyền</p>\r\n\r\n<p>Tygia.vn coi trọng vấn đề bản quyền và sẽ phản hồi lại các thông báo về việc vi phạm bản quyền theo đúng các qui định của pháp luật. Nếu bạn tin rằng bất kỳ thông tin nào cung cấp trên Tygia.vn vi phạm bản quyền của bạn, bạn có thể yêu cầu loại bỏ các thông tin này từ các dịch vụ của Tygia.vn bằng cách gửi thông báo cho Tygia.vn qua thư điện tử hoặc các hình thức liên lạc phù hợp khác.</p>\r\n\r\n<p> </p>\r\n\r\n<p>10. Dựa vào thông tin cung cấp</p>\r\n\r\n<p>Các thông tin có sẵn trên hoặc thông qua Dịch vụ của Tygia.vn được cung cấp duy nhất cho mục đích thông tin chung. Tygia.vn có thể cập nhật các thông tin này theo thời gian, nhưng nội dung của nó không nhất thiết phải hoàn chỉnh hoặc được cập nhật một cách đầy đủ. Bất kỳ thông tin nào có sẵn trên Website Tygia.vn có thể được thay đổi tại bất kỳ thời điểm nào, và Tygia.vn không có nghĩa vụ phải cập nhật thông tin như vậy.</p>\r\n\r\n<p>Tra cứu <a href=\"https://tygia.vn/gia-vang/sjc\" title=\"giá vàng rồng sjc\">giá vàng rồng sjc</a> mới nhất</p>\r\n\r\n<p>Bạn còn thừa nhận và đồng ý rằng: tất cả thông tin công bố trên Tygia.vn (bao gồm những thông tin về bất cứ coupons, mã giảm giá hoặc sản phẩm dịch vụ) được cung cấp bởi các bên thứ ba; Tygia.vn không thể, và từ chối bất kỳ và tất cả các trách nhiệm liên quan đến (i) độ chính xác, hoàn thiện, tin cậy, hiệu quả, sử dụng, hoặc kết quả sử dụng các thông tin công bố trên Tygia.vn (bao gồm những thông tin về bất cứ coupons, mã giảm giá hoặc các sản phẩm, dịch vụ); và bạn chịu trách nhiệm hoàn toàn với bất kỳ hành động nào dựa vào việc sử dụng bất cứ thông tin công bố trên Tygia.vn (bao gồm những thông tin về bất cứ coupons, mã giảm giá hoặc các sản phẩm, dịch vụ).</p>\r\n\r\n<p> </p>\r\n\r\n<p>11. Từ chối các bảo đảm</p>\r\n\r\n<p>Bạn hiểu rằng Tygia.vn không thể và không đảm bảo những tập tin có sẵn để tải về từ Internet hoặc các trang web sẽ không chứa virus hoặc các mã phá hoại khác. Bạn chịu trách nhiệm thực hiện đầy đủ thủ tục kiểm tra để đáp ứng các yêu cầu cụ thể để bảo vệ bạn chống virus và các chương trình phá hoại.</p>\r\n\r\n<p> </p>\r\n\r\n<p>12. Miễn trừ trách nhiệm pháp lý</p>\r\n\r\n<p>Trong bất cứ trường hợp nào, Tygia.vn sẽ không chịu trách nhiệm với bạn hoặc bất kỳ người nào khác/ bên thứ ba nào khác cho mọi, hậu quả phát sinh bên ngoài hoặc liên quan đến những điều khoản sử dụng và/hoặc những dịch vụ của Tygia.vn</p>\r\n\r\n<p> </p>\r\n\r\n<p>Sửa đổi lần cuối: Ngày 30 tháng 04 năm 2018</p>\r\n', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/download.jpg', 'vi', NULL, 0, 3152, 1, NULL, NULL, '2018-04-29 21:24:31', '2019-04-23 11:38:07'),
(1214, 'Từ chối trách nhiệm', 'tu-choi-trach-nhiem-12', '\r\nTygia.vn không có trách nhiệm pháp lý nào về bất kỳ tổn thất hoặc thiệt hại nào phát sinh từ việc sử dụng của tất cả hoặc bất kỳ một phần của website này.\r\n\r\n\r\nHình ảnh cho bài viết Từ chối trách nhiệm\r\n\r\n\r\nTuyên bố từ chối\r\n\r\ntygia.vn cung cấp thô', '<div class=\"blog-content\" itemprop=\"text\">\r\n<div class=\"blog-mt\"><a href=\"https://tygia.vn\">Tygia.vn</a> không có trách nhiệm pháp lý nào về bất kỳ tổn thất hoặc thiệt hại nào phát sinh từ việc sử dụng của tất cả hoặc bất kỳ một phần của website này.</div>\r\n\r\n<div class=\"text-center\"><img alt=\"Từ chối trách nhiệm\" src=\"http://images2-focus-opensocial.googleusercontent.com/gadgets/proxy?container=focus&gadget=a&no_expand=1&rewriteMime=image/*&resize_w=600&url=http://i.imgur.com/Umta7Xi.jpg\" />\r\n<p>Hình ảnh cho bài viết Từ chối trách nhiệm</p>\r\n</div>\r\n\r\n<h2>Tuyên bố từ chối</h2>\r\n\r\n<p>tygia.vn cung cấp thông tin về giá vàng, <a href=\"https://tygia.vn/ty-gia/vietcombank\" title=\"tỷ giá ngoại tệ vietcombank\">tỷ giá ngoại tệ vietcombank</a>, tỷ giá ngoại tệ từ các nguồn đáng tin cậy, từ trang chủ của ngân hàng và các tổ chức lớn về vàng tại Việt Nam.</p>\r\n\r\n<h2>Các nguồn dữ liệu về giá vàng, tỷ giá ngoại tệ tại tygia.vn</h2>\r\n\r\n<table class=\"table table-bordered table-hover\">\r\n	<thead>\r\n		<tr>\r\n			<th>Tên</th>\r\n			<th>Nguồn cung cấp</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>SJC</td>\r\n			<td>sjc.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>DOJI</td>\r\n			<td>doji.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>PNJ</td>\r\n			<td>pnj.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Phú Quý</td>\r\n			<td>phuquy.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bảo Tín Minh Châu</td>\r\n			<td>btmc.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Giá vàng thế giới</td>\r\n			<td>tradingview.com, kitco.com</td>\r\n		</tr>\r\n		<tr>\r\n			<td>ACB</td>\r\n			<td>acb.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Agribank</td>\r\n			<td>agribank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bidv</td>\r\n			<td>bidv.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đông Á Bank</td>\r\n			<td>dongabank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>EximBank</td>\r\n			<td>eximbank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>HSBC</td>\r\n			<td>hsbc.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Mbbank</td>\r\n			<td>mbbank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Sacombank</td>\r\n			<td>sacombank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Techcombank</td>\r\n			<td>techcombank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vietcombank</td>\r\n			<td>vietcombank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Vietinbank</td>\r\n			<td>vietinbank.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Petrolimex</td>\r\n			<td>petrolimex.com.vn</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Giá dầu thô</td>\r\n			<td>tradingview.com</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tiền ảo, tiền điện tử</td>\r\n			<td>coinmarketsum.com</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Dữ liệu được cập nhật liên tục 1 phút 1 lần. Vì đó, dữ liệu được thay đổi liên tục nên chúng tôi không thể đảm bảo tính chính xác của các thông tin trên tùy vào thời điểm nhất định.</p>\r\n\r\n<p>Chúng tôi không chịu trách nhiệm cho việc sai giá dẫn đến yêu cầu bồi thường hoặc khiếu nại khi xem thông tin tại tygia.vn</p>\r\n\r\n<p>Bạn nên xác nhận các <a href=\"https://tygia.vn/ty-gia\" title=\"tỷ giá ở việt nam\">tỷ giá ở việt nam</a>, <a href=\"https://tygia.vn/gia-vang/sjc\" title=\"giá vàng sjc online\">giá vàng sjc online</a> hiện hành trước khi thực hiện bất kỳ giao dịch nào có thể chịu ảnh hưởng do thay đổi trong tỷ giá hối đoái, giá vàng.</p>\r\n\r\n<p>Các tỷ giá, <a href=\"https://tygia.vn/gia-vang\" title=\"giá vàng trực tuyến\">giá vàng trực tuyến</a> chỉ nhằm mục đích cung cấp thông tin, so sánh thông tin và có thể thay đổi mà không cần thông báo. Tỷ giá của các giao dịch thực tế có thể thay đổi.</p>\r\n\r\n<p>Các thông tin trên trang tygia.vn này đã không có liên quan đến mục tiêu đầu tư cụ thể, tình hình tài chính. Thông tin về giá được thực hiện cho bên thứ ba được dựa trên thông tin thu được từ các nguồn tin đáng tin cậy nhưng không đảm bảo là chính xác. tygia.vn không có trách nhiệm pháp lý nào về bất kỳ tổn thất hoặc thiệt hại nào phát sinh từ việc sử dụng của tất cả hoặc bất kỳ một phần của website này.</p>\r\n\r\n<p>(Xin lưu ý, tygia.vn không mua hoặc bán vàng; không mua hoặc bán ngoại tệ. tygia.vn là một trang thông tin, so sánh miễn phí về giá vàng, <a href=\"https://tygia.vn/ty-gia/dab\" title=\"tỷ giá ngoại tệ đông á\">tỷ giá ngoại tệ đông á</a>. Chúng tôi cũng không cung cấp tư vấn đầu tư cá nhân hoặc giá trị của vàng và bạc, đồ trang sức.)</p>\r\nLiên hệ tygia.vn <a href=\"http://tygia.vn/lien-he/\">tại đây</a></div>\r\n', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/images.jpg', 'vi', NULL, 1, 3153, 1, NULL, NULL, '2018-04-29 21:27:37', '2019-07-03 23:38:15'),
(1215, 'Blockchain là gì ?', 'blockchain-la-gi-13', 'Blockchain là một công nghệ cho phép truyền tải dữ liệu một cách an toàn dựa vào hệ thống mã hoá vô cùng phức tạp,  tương tự cuốn sổ cái kế toán của một công ty, nơi mà tiền mặt được giám sát chặt chẽ. Trong trường hợp này Blockchain là một cuốn sổ cái kế toán hoạt động trong lĩnh vực kỹ thuật số', '<p>Blockchain là gì ? Blockchain là một công nghệ cho phép truyền tải dữ liệu một cách an toàn dựa vào hệ thống mã hoá vô cùng phức tạp,  tương tự cuốn sổ cái kế toán của một công ty, nơi mà tiền mặt được giám sát chặt chẽ. Trong trường hợp này Blockchain là một cuốn sổ cái kế toán hoạt động trong lĩnh vực kỹ thuật số.</p>\r\n\r\n<p>Blockchain sở hữu tính năng vô cùng đặc biệt đó là việc truyền tải dữ liệu không đòi hỏi một trung gian để xác nhận thông tin. Hệ thống Blockchain tồn tại rất nhiều nút độc lập có khả năng xác thực thông tin mà không đòi hỏi “dấu hiệu của niềm tin”. Thông tin trong Blockchain không thể bị thay đổi và chỉ được bổ sung thêm khi có sự đồng thuận của tất cả các nút trong hệ thống. Đây là một hệ thống bảo mật an toàn cao trước khả năng bị đánh cắp dữ liệu. Ngay cả khi một phần của hệ thống Blockchain sụp đổ, những máy tính và các nút khác sẽ tiếp tục bảo vệ thông tin và giữ cho mạng lưới tiếp tục hoạt động.</p>\r\n\r\n<p>Công nghệ Blockchain có thể nói là sự kết hợp giữa 3 loại công nghệ bên dưới:</p>\r\n\r\n<ul>\r\n	<li>Mật mã học: Sử dụng public key và hàm hash function để đảm bảo tính minh bạch, toàn vẹn và riêng tư.</li>\r\n	<li>Mạng ngang hàng: Mỗi một nút trong mạng được xem như một client và cũng là server để lưu trữ bản sao ứng dụng.</li>\r\n	<li>Lý thuyết trò chơi: Tất cả các nút tham gia vào hệ thống đều phải tuân thủ luật chơi đồng thuận (PoW, PoS…) và được thúc đẩy bởi động lực kinh tế.</li>\r\n</ul>\r\n\r\n<p>Trên góc độ business có thể gọi là một sổ cái kế toán, hay một cơ sở dữ liệu chứa đựng tài sản, hay một cấu trúc dữ liệu, mà dùng để ghi chép lại lịch sử tài sản giữa các thành viên trong hệ thống mạng ngang hàng.</p>\r\n\r\n<p>Trên góc độ kỹ thuật đó là một phương thức bất biến để lưu trữ lịch sử các giao dịch tài sản.</p>\r\n\r\n<p>Trên góc độ xã hội đó là một hiện tượng, mà dùng để thiết lập niềm tin bằng quy tắc đồng thuận giữa các thành viên trong một hệ thống phân cấp.</p>\r\n\r\n<p> </p>\r\n\r\n<p>Ý tưởng ra đời</p>\r\n\r\n<p>Bắt nguồn từ bài toán Các vị tướng Byzantine (Byzantine Generals) trong ngành khoa học máy tính và xử lý đường truyền tin cậy trong một hệ thống phân cấp.</p>\r\n\r\n<p>Nội dung bài toán mô tả: Một đạo quân đi chiếm thành và các vị tướng nằm ở nhiều vị trí khác nhau. Trong đó có N tướng trung thành muốn chiếm thành và M tuớng phản bội muốn rút binh, một tướng phản bội truyền tin cho một nhóm là tấn công và truyền tin cho nhóm khác là rút binh. Vậy làm sao để các tướng có thể nhất quán thông tin và cùng nhau chiếm thành? Chỉ cần một sơ xuất trong việc truyền tin có thể khiến cả đạo quân có thể bị tiêu diệt.</p>\r\n\r\n<p>Nếu đồng loạt tấn công vào thành thì sẽ chiến thắng, bằng không tất cả sẽ bị tiêu diệt.</p>\r\n\r\n<p>Bài toán Các vị tướng Byzantine này vẫn chưa ai có thể đưa ra lời giải. Do đó chúng ta cần phải có một bên thứ ba để xây dựng lòng tin. Ví dụ như trong bài toán trên, cần có một bên thứ ba đứng ra làm thoả thuận để các tướng lĩnh ký tên vào, nếu vị tướng nào làm trái thoả thuận sẽ bị trừng phạt. Bên thứ ba đảm bảo cho việc chiếm thành của các vị tướng là đồng loạt, bởi vì các tướng có thể không tin nhau nhưng bắt buộc phải tin tưởng tuyệt đối vào bên thứ ba này.</p>\r\n\r\n<p>Đây là ý tưởng mở đầu cho một hệ thống Blockchain có thể giúp các vị tướng tin tưởng nhau hơn.</p>\r\n\r\n<p>Sau cuộc khủng hoảng tài chính năm 2008, hệ thống tài chính Mỹ sụp đổ hoàn toàn khiến người dân đánh mất niềm tin vào đồng tiền của một bên thứ ba đáng tin cậy. Ý tưởng về Bitcoin – một đồng tiền phân cấp ngang hàng trên mạng máy tính lần đầu tiên được Satoshi Nakamoto đưa ra, cũng là ứng dụng đầu tiên của Blockchain.</p>\r\n\r\n<p>Các loại Blockchain</p>\r\n\r\n<p>Hệ thống Blockchain chia thành 3 loại chính:</p>\r\n\r\n<ul>\r\n	<li><strong>Public:</strong> Bất kỳ ai cũng có quyền đọc và ghi dữ liệu trên Blockchain. Quá trình xác thực giao dịch trên Blockchain này đòi hỏi phải có hàng nghìn hay hàng vạn nút tham gia. Do đó để tấn công vào hệ thống Blockchain này là điều bất khả thi vì chi phi khá cao. Ví dụ: Bitcoin, Ethereum…</li>\r\n	<li><strong>Private:</strong> Người dùng chỉ được quyền đọc dữ liệu, không có quyền ghi vì điều này thuộc về bên tổ chức thứ ba tuyệt đối tin cậy. Tổ chức này có thể hoặc không cho phép người dùng đọc dữ liệu trong một số trường hợp. Bên thứ ba toàn quyền quyết định mọi thay đổi trên Blockchain. Vì đây là một Private Blockchain, cho nên thời gian xác nhận giao dịch khá nhanh vì chỉ cần một lượng nhỏ thiết bị tham gia xác thực giao dịch. Ví dụ: Ripple là một dạng Private Blockchain, hệ thống này cho phép 20% các nút là gian dối và chỉ cần 80% còn lại hoạt động ổn định là được.</li>\r\n	<li><strong>Permissioned:</strong> Hay còn gọi là <strong>Consortium</strong>, một dạng của Private nhưng bổ sung thêm một số tính năng nhất định, kết hợp giữa “niềm tin” khi tham gia vào Public và “niềm tin tuyệt đối” khi tham gia vào Private. Ví dụ: Các ngân hàng hay tổ chức tài chính liên doanh sẽ sử dụng Blockchain cho riêng mình.</li>\r\n</ul>\r\n\r\n<p>Các phiên bản của Blockchain</p>\r\n\r\n<ul>\r\n	<li><strong>Blockchain 1.0 – Tiền tệ và Thanh toán:</strong> Ứng dụng chính của phiên bản này là tiền mã hoá: bao gồm việc chuyển đổi tiền tệ, kiều hối và tạo lập hệ thống thanh toán kỹ thuật số. Đây cũng là lĩnh vực quen thuộc với chúng ta nhất mà đôi khi khá nhiều người lầm tưởng Bitcoin và Blockchain là một.</li>\r\n	<li><strong>Blockchain 2.0 – Tài chính và Thị trường:</strong> Ứng dụng xử lý tài chính và ngân hàng: mở rộng quy mô của Blockchain, đưa vào các ứng dụng tài chính và thị trường. Các tài sản bao gồm cổ phiếu, chi phiếu, nợ, quyền sở hữu và bất kỳ điều gì có liên quan đến thỏa thuận hay hợp đồng.</li>\r\n	<li><strong>Blockchain 3.0 – Thiết kế và Giám sát hoạt động:</strong>  Đưa Blockchain vượt khỏi biên giới tài chính, và đi vào các lĩnh vực như giáo dục, chính phủ, y tế và nghệ thuật. Ở những lĩnh vực này sẽ là lại có nhiều loại như physical, digital hay human in nature.</li>\r\n</ul>\r\n\r\n<p>Cơ chế đồng thuận trong Blockchain</p>\r\n\r\n<p>Cơ chế đồng thuận trong Blockchain có thể hiểu như cách thức mà các vị tướng Byzantine có thể đạt đồng thuận để cùng nhau chiếm thành. Sau đây là các loại cơ chế đồng thuận phổ biến:</p>\r\n\r\n<ul>\r\n	<li><strong>Proof of Work (Bằng chứng Công việc): </strong>Phổ biến trong Bitcoin, Ethereum, Litecoin, Dogecoin và hầu hết các loại tiền mã hoá. Tiêu tốn khá nhiều năng lượng điện.</li>\r\n	<li><strong>Proof of Stake (Bằng chứng Cổ phần): </strong>Phổ biến trong Decred, Peercoin và trong tương lai là Ethereum và nhiều loại tiền mã hoá khác. Phân cấp hơn, tiêu hao ít năng lượng và không dễ gì bị đe doạ.</li>\r\n	<li><strong>Delegated Proof-of-Stake (Uỷ quyền Cổ phần): </strong>Phổ biến trong Steemit, EOS, BitShares. Chi phí giao dịch rẻ; có khả năng mở rộng; hiệu suất năng lượng cao. Tuy nhiên vẫn một phần hơi hướng tập trung vì thuật toán này lựa chọn người đáng tin cậy để uỷ quyền.</li>\r\n	<li><strong>Proof of Authority (Bằng chứng Uỷ nhiệm): </strong>Đây là mô hình tập trung thường thấy trong POA.Network, Ethereum Kovan testnet. Hiệu suất cao, có khả năng mở rộng tốt.</li>\r\n	<li><strong>Proof-of-Weight (Bằng chứng Khối lượng / Càng lớn càng tốt): </strong>Phổ biến trong Algorand, Filecoin. Có thể tuỳ chỉnh và khả năng mở rộng tốt. Tuy nhiên quá trình thúc đẩy việc phát triển sẽ là một thử thách lớn.</li>\r\n	<li><strong>Byzantine Fault Tolerance (Đồng thuận chống gian lận / Tướng Byzantine bao vây Blockchain): </strong>Phổ biến trong Hyperledger, Stellar, Dispatch, và Ripple. Năng suất cao; chi phí thấp; có khả năng mở rộng. Tuy nhiên vẫn chưa thể tin tưởng hoàn toàn. Thuật toán này có 2 phiên bản là:\r\n	<ul>\r\n		<li><strong>Practical Byzantine Fault Tolerance (Đồng thuận chống gian lận / Tướng Byzantine bao vây Blockchain trong thực tế)</strong></li>\r\n		<li><strong>Federated Byzantine Agreement (Liên minh Byzantine cùng đồng thuận)</strong></li>\r\n	</ul>\r\n	</li>\r\n	<li><strong>Directed Acyclic Graphs</strong><strong> (Thuật toán tô pô): </strong>Thường thấy trong Iota (<a href=\"https://bitcoinvietnamnews.com/2017/12/iota-miota-la-gi-suc-manh-cua-cong-nghe-tangle.html\" target=\"_self\">công nghệ Tangle</a>), Hashgraph, Raiblocks/Nano (<a href=\"https://bitcoinvietnamnews.com/2018/01/raiblocks-la-gi.html\" target=\"_self\">công nghệ <strong>Block-lattice</strong></a>), là một đối thủ của Blockchain.</li>\r\n</ul>\r\n\r\n<p>Đặc điểm chính của Blockchain</p>\r\n\r\n<ul>\r\n	<li><strong><em>Không thể làm giả, không thể phá hủy các chuỗi Blockchain: </em></strong>theo như lý thuyết thì chỉ có máy tính lượng tử mới có thể giải mã Blockchain và công nghệ Blockchain biến mất khi không còn Internet trên toàn cầu.</li>\r\n	<li><strong><em>Bất biến:</em></strong> dữ liệu trong Blockchain không thể sửa (có thể sửa nhưng sẽ để lại dấu vết) và sẽ lưu trữ mãi mãi.</li>\r\n	<li><em><strong>Bảo mật: </strong></em>Các thông tin, dữ liệu trong Blockchain được phân tán và an toàn tuyệt đối.</li>\r\n	<li><em><strong>Minh bạch:</strong></em> Ai cũng có thể theo dõi dữ liệu Blockchain đi từ địa chỉ này tới địa chỉ khác và có thể thống kê toàn bộ lịch sử trên địa chỉ đó.</li>\r\n	<li><em><strong>Hợp đồng Thông minh:</strong></em> là hợp đồng kỹ thuật số được nhúng vào đoạn code if-this-then-that (IFTTT), cho phép chúng tự thực thi mà không cần bên thứ ba.\r\n	<ul>\r\n		<li>Xem thêm: <a href=\"https://bitcoinvietnamnews.com/smart-contract-la-gi\" target=\"_self\">Smart Contract / Hợp đồng Thông minh là gì?</a></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>Tương lai của công nghệ Blockchain</p>\r\n\r\n<p>Sự xuất hiện của Blockchain cũng như các cột mốc khi máy tính cá nhân hoặc Internet ra đời, hệ thống này sẽ thay đổi cách mà chúng ta hiểu biết và nhìn nhận xã hội.</p>\r\n\r\n<p>Tiềm năng lớn nhất chính là tạo nơi áp dụng <a href=\"https://bitcoinvietnamnews.com/smart-contract-la-gi\" target=\"_self\">Hợp đồng Thông minh</a>: các thoả thuận trong hợp đồng và giao dịch sẽ được xác nhận mà không tiết lộ thông tin giữa các bên với một người trung gian nào đó mà vẫn đảm bảo mọi thứ là minh bạch và chắc chắn nhất.</p>\r\n\r\n<p>Thông tin trong Blockchain không thể bị làm giả (có thể nhưng vẫn sẽ để lại dấu vết), mọi thay đổi cần phải nhận được sự đồng thuận của tất cả các nút tham gia trong hệ thống. Nó là một hệ thống không dễ dàng sụp đổ, vì ngay cả khi một phần mạng lưới tê liệt thì các nút khác vẫn sẽ tiếp tục hoạt động để bảo vệ thông tin.</p>\r\n\r\n<p>Công nghệ Blockchain mở ra một xu hướng mới cho các lĩnh vực như tài chính ngân hàng, logistics, điện tử viễn thông, kế toán kiểm toán…</p>\r\n\r\n<p>Không chỉ thế Blockchain còn là nòng cốt của Internet vạn vật (IoT). Các thiết bị điện tử có thể giao tiếp một cách an toàn và minh bạch, những nỗ lực bất chính trong thế giới Internet sẽ không thực hiện được, và còn nhiều điều nữa…</p>\r\n\r\n<p>Hiện nay có rất nhiều công ty và tập đoàn lớn đang xây dựng mạng lưới Blockchain cho riêng mình. Vì thế chúng ta sẽ sớm thấy điều này có thể tạo ra một làn sóng cho tương lai.</p>\r\n\r\n<p>Ngoài ra bạn có thể cập nhật những tin tức liên quan đến Blockchain và tiền điện tử nóng hổi nhất bằng cách follow Bitcoin Vietnam News trên Telegram nhé: <a href=\"https://t.me/bitcoin_vietnam_news\" target=\"_blank\">https://t.me/bitcoin_vietnam_news</a>.</p>', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/Blockchain-1.jpg', 'vi', NULL, 1, 3154, 1, NULL, NULL, '2018-07-03 22:19:36', '2019-07-04 09:55:22'),
(1217, 'Hiểu đúng về Xăng sinh học E5, E10. Có hại máy móc không?', 'hieu-dung-ve-xang-sinh-hoc-e5-e10-co-hai-may-moc-khong-15', NULL, '<p>Việc phát triển nhiên liệu sinh học giúp các quốc gia chủ động, không bị lệ thuộc vào vấn đề nhập khẩu nhiên liệu, đặc biệt với những quốc gia không có nguồn dầu mỏ và than đá. Đồng thời, kiềm chế sự gia tăng giá dầu, ổn định tinh hình năng lượng cho thế giới.</p>\r\n\r\n<p><strong>Vậy, xăng sinh học là gì?</strong></p>\r\n\r\n<ul>\r\n	<li>Xăng sinh học là hỗn hợp của xăng truyền thống và cồn sinh học (bioethanol) được sử dụng cho các loại động cơ xăng đốt trong như xe ô tô và xe gắn máy.</li>\r\n	<li>Nguyên liệu chính để sản xuất cồn sinh học tại Việt Nam hiện nay là sắn lát khô.</li>\r\n	<li>Cồn sinh học trong hỗn hợp nhiên liệu sinh học được sử dụng như một chất chứa oxy thay thế cho các hợp chất pha vào xăng trước đây như chì hay ete. Cồn sinh học được sản xuất từ quá trình lên men tinh bột, mật rỉ đường và các phế phẩm nông nghiệp khác.</li>\r\n</ul>\r\n\r\n<p><strong>Về xăng sinh học E5, E10</strong></p>\r\n\r\n<ul>\r\n	<li>Xăng sinh học được ký hiệu là Ex trong đó x là % thể tich cồn trong công thức pha trộn xăng sinh học.</li>\r\n	<li>Xăng sinh học E5 là nhiên liệu chứa 5% thể tich cồn sinh học và 95% thể tich xăng truyền thống.</li>\r\n	<li>Xăng sinh học E10 là nhiên liệu chứa 10% thể tich cồn sinh học và 90% thể tich xăng truyền thống.</li>\r\n</ul>\r\n\r\n<p><strong>Ảnh hưởng của xăng sinh học đến động cơ như thế nào?</strong></p>\r\n\r\n<p>-Việc sử dụng xăng sinh học E5 giúp cải thiện tinh năng động cơ, giảm phát thải, mang lại lợi ích cho người tiêu dùng và xã hội. Quá trình sử dụng xăng sinh học E5 rất thuận tiện, không cần phải điều chỉnh động cơ khi chuyển đổi giữa xăng sinh học E5 và xăng thông thường.</p>\r\n\r\n<p>-Do ethanol có trị số octan cao tới 109 nên khi pha vào xăng sẽ làm tăng trị số octan và tăng khả năng chống kích nổ của nhiên liệu. Thêm vào đó, với hàm lượng oxy cao hơn xăng thông dụng, giúp quá trình cháy trong động cơ diễn ra triệt để hơn, tăng công suất, giảm tiêu hao nhiên liệu, đồng thời giảm thiểu phát thải các chất độc hại trong khí thải động cơ.</p>\r\n\r\n<p>Đó là lý do vì sao nhiên liệu xăng sinh học được coi là nhiên liệu của tương lai, được cả thế giới quan tâm. Cần lưu ý là nếu sử dụng nhiên liệu xăng có hàm lượng ethanol cao hơn 10% có thể gây ảnh hưởng đến một số chi tiết kim loại, cao su, nhựa, polymer của động cơ. Tuy nhiên, với hàm lượng 5% ethanol trong xăng sinh học E5 thì các ảnh hưởng này không xảy ra.</p>\r\n\r\n<p><strong>Ảnh hưởng của xăng sinh học đến môi trường như thế nào?</strong></p>\r\n\r\n<p>Như chúng ta đã biết, khí thải CO là một khí rất độc, mức phát thải CO rất cao ở động cơ xe máy. Theo các kết quả nghiên cứu, động cơ sử dụng xăng sinh học E5 tạo ra rất ít khí thải CO và HC, ít hơn hẳn các loại xăng thông dụng như A92 và A95 tới 20%. Chính vì vậy, loại xăng sinh học E5 được coi là thân thiện với môi trường.</p>\r\n\r\n<p>Bên cạnh giảm đáng kể thành phần khí CO và HC, khả năng tăng tốc của xe cũng tốt hơn đối với xăng sinh học E5. Quá trình cháy trong động cơ sử dụng xăng sinh học E5 được cải thiện nhờ hỗn hợp giữa không khí và nhiên liệu đồng đều hơn do khả năng bay hơi tốt của xăng sinh học E5.</p>\r\n\r\n<p>Ngoài ra, sự có mặt của thành phần oxy trong xăng sinh học E5 là yếu tố giúp cho nhiên liệu được cháy trong điều kiện không quá thiếu oxy (cháy với hỗn hợp nhạt hơn so với trường hợp động cơ xăng dùng bộ chế hòa khí sử dụng nhiên liệu xăng RON92) và cháy kiệt. Đây là cơ sở tạo ra ít khí thải độc hại CO và HC. Thêm vào đó, các loại xe thế hệ mới hiện nay có bộ phận xử lý khí thải, kết hợp với sử dụng xăng sinh học E5 thì lượng khí độc thải ra môi trường sẽ giảm đáng kể.</p>', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/Bitcoin-là-gì.png', 'vi', NULL, 1, 3156, 1, NULL, NULL, '2018-07-03 23:32:44', '2019-07-03 22:47:04'),
(1218, 'Ai kiểm soát Hệ thống Bitcoin', 'ai-kiem-soat-he-thong-bitcoin-16', 'Không ai có thể kiểm soát hoặc sở hữu được hệ thống bitcoin giống như việc họ không thể kiểm soát và sở hữu được công nghệ đằng sau email và internet. Tất cả các giao dịch bitcoin được xác nhận bởi các thợ mỏ đào bitcoin, và nó đã phát triển thành cả', '<p style=\"text-align: left;\"><span style=\"color: rgb(34, 34, 34); font-family: Verdana, Geneva, sans-serif; font-size: 15px;\">Không ai có thể kiểm soát hoặc sở hữu được hệ thống bitcoin giống như việc họ không thể kiểm soát và sở hữu được công nghệ đằng sau email và internet. Tất cả các giao dịch bitcoin được xác nhận bởi các thợ mỏ đào bitcoin, và nó đã phát triển thành cả một nền công nghiệp đào bitcoin.</span></p>\r\n\r\n<p> </p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"/storage/userfiles/images/news/phuong-thuc-giao-dich-bitcoin.png\" /></p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\"> </p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Nhưng phía sau đó, mạng lưới Bitcoin chia sẻ một sổ cái công cộng gọi là “chuỗi khối”. sổ cái này chứa tất cả các giao dịch được xử lý trước đó và cho phép máy tính của người dùng để xác minh tính hợp lệ của từng giao dịch. Tính xác thực của mỗi giao dịch được bảo vệ bằng chữ ký kỹ thuật số tương ứng với các địa chỉ gửi do đó cho phép tất cả người dùng có toàn quyền kiểm soát việc gửi Bitcoins.</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Bạn có thể tra cứu: <a href=\"https://tygia.vn/gia-vang/pnj\" title=\"giá vàng pnj\"><span style=\"color:#B22222;\">giá vàng pnj</span></a></p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Vì vậy, không có gian lận, không có các khoản bồi hoàn và không có thông tin xác định rằng có thể bị tổn hại dẫn đến hành vi trộm cắp danh tính.</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\"><strong>Tại sao lại chỉ có 21 triệu bitcoin ?</strong></p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px;\"><span style=\"color: rgb(34, 34, 34);\">Câu hỏi mà rất nhiều người đặt ra đối với đồng tiề</span><span style=\"background-color: rgb(255, 255, 255);\">n này,</span><span style=\"color: rgb(34, 34, 34);\"><span style=\"background-color: rgb(255, 255, 255);\"> </span>tại sao lại chỉ có 21 triệu, tại sao không tạo ra 1 tỷ bitcoin ?</span></p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Xét về chức năng thì chúng ta có thể coi bitcoin cũng vàng, kim cương, … những cái gì càng hiếm thì giá trị nó càng cao. Nhưng tại sao lại là con số 21 triệu thì có lẽ phải hỏi người viết ra rồi ( mà người viết ra hiện nay vẫn còn khá nhiều chi tiết chưa được rõ ).</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\"> </p>\r\n\r\n<p style=\"text-align: center; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\"> <img src=\"/storage/userfiles/images/news/phan-tich-giao-dich-bitcoin.png\" style=\"float: none;\" /></p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Chúng ta biết rằng giá trị bitcoin sẽ được giải mã ra như sau:</p>\r\n\r\n<pre style=\"font-family: monospace, monospace; font-size: 15px; background-color: rgb(241, 241, 241); padding: 4px 10px; border-width: initial; border-style: none; border-color: initial; border-radius: 0px; margin-top: 24px; color: rgb(34, 34, 34);\">\r\n<code style=\"font-family: monospace, monospace; font-size: 1em; position: relative; top: -1px; border: none; background-color: rgb(241, 241, 241); padding: 2px 6px;\">6 blocks mỗi giờ\r\n* 24h/ ngày\r\n* 365 ngày/ năm\r\n* 4 năm cho một chu kỳ  ( cái này hỏi thánh nào code ra nhé )\r\n= 210,240\r\n~= 210, </code></pre>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Tổng giá trị giải thưởng bitcoin cho tất cả các blocks là 100 và phần thưởng sẽ giảm dần:</p>\r\n\r\n<pre style=\"font-family: monospace, monospace; font-size: 15px; background-color: rgb(241, 241, 241); padding: 4px 10px; border-width: initial; border-style: none; border-color: initial; border-radius: 0px; margin-top: 24px; color: rgb(34, 34, 34);\">\r\n<code style=\"font-family: monospace, monospace; font-size: 1em; position: relative; top: -1px; border: none; background-color: rgb(241, 241, 241); padding: 2px 6px;\">50 + 25 + 12.5 + 6.25 + 3.125 + ... = 100</code></pre>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">==> 210.000 * 100 = 21.000.000 bitcoins</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Thật ra việc giải mã sẽ dừng lại cho đến khi bitcoins chỉ còn dưới 1 satoshi (= 1/1 triệu btc) thì việc giải mã sẽ dừng lại.</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Vậy khi đó phần thưởng cho những thợ mỏ đào bitcoin là phí cho mỗi giao dịch.</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">==> Nếu bạn gửi bitcoin mà muốn confirmed nhanh thì chỉ có cách là bạn nâng phí gửi lên. Cái này chúng ta có thể chỉnh được trong các giao dịch bitcoin nhé.</p>\r\n\r\n<p style=\"font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-bottom: 26px; color: rgb(34, 34, 34);\">Xem thêm thị trường: <a href=\"https://tygia.vn/ty-gia/bidv\" title=\"tỷ giá bidv\"><span style=\"color:#A52A2A;\">tỷ giá bidv</span></a></p>\r\n', 'Administrator', 'quantri@tygia.vn', '/storage/userfiles/images/news/phan-tich-giao-dich-bitcoin.png', 'vi', NULL, 2, 3157, 1, NULL, NULL, '2018-07-04 20:54:01', '2019-07-04 06:07:43');
